class CommonTranslateLoader {
  /**
   * @param {?=} translations
   */
  constructor(translations = {}) {
    this.translations = {
      'ar': AR,
      'ca': CA,
      'cs': CS,
      'da': DA,
      'de': DE,
      'el': EL,
      'en': EN,
      'es': ES,
      'fil': FIL,
