      'he': HE,
      'hi': HI,
      'id': ID,
      'it': IT,
      'ja': JA,
      'kk': KK,
      'ko': KO,
      'ms': MS,
      'nl': NL,
      'pl': PL,
      'pt': PT,
      'ro': RO,
      'ru': RU,
      'sv': SV,
      'th': TH,
      'tr': TR,
      'uk': UK,
      'vi': VI,
      'zh-hans': ZHHANS,
      'zh-hant': ZHHANT
    };
    for (const key in translations) {
      if (this.translations[key]) {
        this.translations[key] = Object.assign({}, this.translations[key], translations[key]);
      }
    }
  }
  /**
   * @param {?} lang
   * @return {?}
   */
  getTranslation(lang) {
    /** @type {?} */
    const translation = this.translations[lang] ? this.translations[lang] : this.translations[this.defaultLanguage];
    return new Observable(
    /**
    * @param {?} subscriber
    * @return {?}
    */
    subscriber => {
      subscriber.next(translation);
    });
  }
}
